import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { logout } from '../features/auth/authSlice';
import { getGroups, reset } from '../features/groups/groupSlice';

const DashboardPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user } = useSelector((state) => state.auth);
  const { groups, isLoading, isError, message } = useSelector(
    (state) => state.groups
  );

  useEffect(() => {
    if (isError) {
      console.log(message);
    }

    if (!user) {
      navigate('/login');
    }

    dispatch(getGroups());

    return () => {
      dispatch(reset());
    };
  }, [user, navigate, isError, message, dispatch]);

  const handleLogout = () => {
    dispatch(logout());
    navigate('/login');
  };
  
  if (isLoading) {
    return <h2>Loading...</h2>
  }

  return (
    <div className="p-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Dashboard</h1>
        <button
          onClick={handleLogout}
          className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700"
        >
          Logout
        </button>
      </div>
      <p className="mb-4">Welcome to your SplitIQ dashboard, {user && user.name}!</p>
      
      <h2 className="text-2xl font-bold mb-4">Your Groups</h2>
      {groups.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {groups.map((group) => (
            <div key={group._id} className="p-4 bg-white rounded-lg shadow-md">
              <h3 className="text-xl font-bold">{group.name}</h3>
              <p className="text-gray-600">{group.description}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>You are not a member of any groups yet.</p>
      )}
    </div>
  );
};

export default DashboardPage;
