import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { getExpensesForGroup, getGroupBalances, addExpense, reset } from '../features/expenses/expenseSlice';
import Modal from '../components/common/Modal';

const GroupDetailPage = () => {
  const { groupId } = useParams();
  const dispatch = useDispatch();

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');

  const { user } = useSelector((state) => state.auth);
  const { expenses, balances, isLoading, isError, message } = useSelector(
    (state) => state.expenses
  );
  const { groups } = useSelector((state) => state.groups);
  const group = groups.find((g) => g._id === groupId);

  useEffect(() => {
    if (isError) {
      console.log(message);
    }
    dispatch(getExpensesForGroup(groupId));
    dispatch(getGroupBalances(groupId));
    return () => {
      dispatch(reset());
    };
  }, [groupId, isError, message, dispatch]);

  const handleAddExpense = (e) => {
    e.preventDefault();
    const expenseData = {
      description,
      amount: Number(amount),
      group: groupId,
      paidBy: user._id, // Assuming the logged-in user is the payer for simplicity
      split_method: 'equally',
    };
    dispatch(addExpense(expenseData));
    setIsModalOpen(false);
    setDescription('');
    setAmount('');
  };

  if (isLoading) {
    return <h2>Loading...</h2>;
  }

  return (
    <div className="p-8">
      <Link to="/" className="text-indigo-600 hover:text-indigo-800 mb-4 inline-block">
        &larr; Back to Dashboard
      </Link>
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">{group ? group.name : 'Group Details'}</h1>
        <button
          onClick={() => setIsModalOpen(true)}
          className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700"
        >
          Add Expense
        </button>
      </div>
      
      <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-8">
        <div>
          <h2 className="text-2xl font-bold mb-4">Expenses</h2>
          {expenses.length > 0 ? (
            <ul className="space-y-4">
              {expenses.map((expense) => (
                <li key={expense._id} className="p-4 bg-white rounded-lg shadow-md">
                  <div className="flex justify-between">
                    <span className="font-bold">{expense.description}</span>
                    <span className="font-bold">₹{expense.amount.toFixed(2)}</span>
                  </div>
                  <div className="text-sm text-gray-600">
                    Paid by {expense.paidBy.name}
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No expenses have been added to this group yet.</p>
          )}
        </div>

        <div>
          <h2 className="text-2xl font-bold mb-4">Balances</h2>
          {balances.length > 0 ? (
            <ul className="space-y-2">
              {balances.map((member) => (
                <li key={member.email} className="flex justify-between p-2 rounded-md">
                  <span>{member.name}</span>
                  <span className={member.balance >= 0 ? 'text-green-600' : 'text-red-600'}>
                    {member.balance >= 0 ? `Gets back ₹${member.balance.toFixed(2)}` : `Owes ₹${Math.abs(member.balance).toFixed(2)}`}
                  </span>
                </li>
              ))}
            </ul>
          ) : (
            <p>Balances will be calculated once expenses are added.</p>
          )}
        </div>
      </div>

      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <h3 className="text-xl font-bold mb-4">Add a New Expense</h3>
        <form onSubmit={handleAddExpense}>
          <div className="mb-4">
            <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
            <input
              type="text"
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="amount" className="block text-sm font-medium text-gray-700">Amount (₹)</label>
            <input
              type="number"
              id="amount"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              required
            />
          </div>
          <div className="flex justify-end">
            <button type="button" onClick={() => setIsModalOpen(false)} className="mr-2 px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300">
              Cancel
            </button>
            <button type="submit" className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700">
              Add
            </button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default GroupDetailPage;
