  return (
    <button
      onClick={toggleTheme}
      className="p-2 rounded-md text-primary dark:text-dark-primary bg-gray-200 dark:bg-dark-accent"
    >
      {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
    </button>
  );
};

export default ThemeToggleButton;
