// src/pages/GroupDetailPage.js

import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { getExpensesForGroup, getGroupBalances, addExpense, reset as resetExpenses } from '../features/expenses/expenseSlice';
import { getGroupById, addMemberToGroup, reset as resetGroups } from '../features/groups/groupSlice'; // Import new actions
import Modal from '../components/common/Modal';
import axios from 'axios'; // Import axios for user search

const GroupDetailPage = () => {
  const { groupId } = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Local state for modals and forms
  const [isExpenseModalOpen, setIsExpenseModalOpen] = useState(false);
  const [isMemberModalOpen, setIsMemberModalOpen] = useState(false);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  // Redux state
  const { user, token } = useSelector((state) => state.auth);
  const { expenses, balances, isLoading: isExpensesLoading, isError: isExpensesError, message: expensesMessage } = useSelector(
    (state) => state.expenses
  );
  const { singleGroup, isLoading: isGroupsLoading, isError: isGroupsError, message: groupsMessage } = useSelector(
    (state) => state.groups
  );

  useEffect(() => {
    if (isExpensesError) console.log('Expense Error:', expensesMessage);
    if (isGroupsError) console.log('Group Error:', groupsMessage);

    if (!user) {
      navigate('/login');
    } else {
      dispatch(getGroupById(groupId));
      dispatch(getExpensesForGroup(groupId));
      dispatch(getGroupBalances(groupId));
    }

    return () => {
      dispatch(resetExpenses());
      dispatch(resetGroups());
    };
  }, [groupId, user, navigate, isExpensesError, isGroupsError, expensesMessage, groupsMessage, dispatch]);

  const handleAddExpense = (e) => {
    e.preventDefault();
    const expenseData = {
      description,
      amount: Number(amount),
      group: groupId,
      paidBy: user._id,
      split_method: 'equally',
    };
    dispatch(addExpense(expenseData));
    setIsExpenseModalOpen(false);
    setDescription('');
    setAmount('');
  };

  const handleSearchUsers = async (e) => {
    setSearchQuery(e.target.value);
    if (e.target.value.length > 2) {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const response = await axios.get(`http://localhost:5000/api/v1/users?search=${e.target.value}`, config);
        // Filter out users who are already members
        const existingMemberIds = singleGroup.members.map(m => m._id);
        const filteredResults = response.data.data.filter(u => !existingMemberIds.includes(u._id));
        setSearchResults(filteredResults);
      } catch (error) {
        console.error('Failed to search users', error);
      }
    } else {
      setSearchResults([]);
    }
  };
  
  const handleAddMember = () => {
    if (selectedUser) {
      dispatch(addMemberToGroup({ groupId, userId: selectedUser._id }));
      setIsMemberModalOpen(false);
      setSearchQuery('');
      setSearchResults([]);
      setSelectedUser(null);
    }
  };

  const isLoading = isExpensesLoading || isGroupsLoading;

  if (isLoading || !singleGroup) {
    return <h2>Loading...</h2>;
  }

  return (
    <div className="p-8">
      <Link to="/" className="text-indigo-600 hover:text-indigo-800 mb-4 inline-block">
        &larr; Back to Dashboard
      </Link>
      <div className="flex justify-between items-center mb-4">
        <div>
            <h1 className="text-3xl font-bold">{singleGroup.name}</h1>
            <div className="flex items-center mt-2">
                <span className="mr-2 font-semibold">Members:</span>
                {singleGroup.members.map(member => (
                    <img key={member._id} src={member.avatar} alt={member.name} title={member.name} className="w-8 h-8 rounded-full border-2 border-white -ml-2"/>
                ))}
            </div>
        </div>
        <div>
            <button onClick={() => setIsMemberModalOpen(true)} className="px-4 py-2 text-sm font-medium text-white bg-gray-600 rounded-md hover:bg-gray-700 mr-2">
                Add Member
            </button>
            <button onClick={() => setIsExpenseModalOpen(true)} className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700">
                Add Expense
            </button>
        </div>
      </div>
      
      {/* Main Content Grid */}
      <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Expenses List */}
        <div>
          <h2 className="text-2xl font-bold mb-4">Expenses</h2>
          {expenses.length > 0 ? (
            <ul className="space-y-4">
              {expenses.map((expense) => (
                <li key={expense._id} className="p-4 bg-white rounded-lg shadow-md">
                  <div className="flex justify-between">
                    <span className="font-bold">{expense.description}</span>
                    <span className="font-bold">₹{expense.amount.toFixed(2)}</span>
                  </div>
                  <div className="text-sm text-gray-600">
                    Paid by {expense.paidBy.name}
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No expenses have been added to this group yet.</p>
          )}
        </div>

        {/* Balances List */}
        <div>
          <h2 className="text-2xl font-bold mb-4">Balances</h2>
          {balances.length > 0 ? (
            <ul className="space-y-2">
              {balances.map((member) => (
                <li key={member.email} className="flex justify-between p-2 rounded-md bg-white shadow-sm">
                  <span>{member.name}</span>
                  <span className={member.balance >= 0 ? 'text-green-600 font-semibold' : 'text-red-600 font-semibold'}>
                    {member.balance >= 0 ? `Gets back ₹${member.balance.toFixed(2)}` : `Owes ₹${Math.abs(member.balance).toFixed(2)}`}
                  </span>
                </li>
              ))}
            </ul>
          ) : (
            <p>Balances will be calculated once expenses are added.</p>
          )}
        </div>
      </div>

      {/* Add Expense Modal */}
      <Modal isOpen={isExpenseModalOpen} onClose={() => setIsExpenseModalOpen(false)}>
        <h3 className="text-xl font-bold mb-4">Add a New Expense</h3>
        <form onSubmit={handleAddExpense}>
          {/* Form fields for description and amount */}
          <div className="mb-4">
            <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
            <input type="text" id="description" value={description} onChange={(e) => setDescription(e.target.value)} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" required />
          </div>
          <div className="mb-4">
            <label htmlFor="amount" className="block text-sm font-medium text-gray-700">Amount (₹)</label>
            <input type="number" id="amount" value={amount} onChange={(e) => setAmount(e.target.value)} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" required />
          </div>
          <div className="flex justify-end">
            <button type="button" onClick={() => setIsExpenseModalOpen(false)} className="mr-2 px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300">Cancel</button>
            <button type="submit" className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700">Add</button>
          </div>
        </form>
      </Modal>

      {/* Add Member Modal */}
      <Modal isOpen={isMemberModalOpen} onClose={() => setIsMemberModalOpen(false)}>
        <h3 className="text-xl font-bold mb-4">Add a Member</h3>
        <div>
            <label htmlFor="userSearch" className="block text-sm font-medium text-gray-700">Search for a user by name or email</label>
            <input type="text" id="userSearch" value={searchQuery} onChange={handleSearchUsers} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" placeholder="Start typing..."/>
        </div>
        <ul className="mt-4 max-h-40 overflow-y-auto">
            {searchResults.map(u => (
                <li key={u._id} onClick={() => { setSelectedUser(u); setSearchQuery(u.name); setSearchResults([]); }} className="p-2 hover:bg-gray-100 cursor-pointer rounded-md">
                    {u.name} ({u.email})
                </li>
            ))}
        </ul>
        <div className="flex justify-end mt-4">
            <button type="button" onClick={() => setIsMemberModalOpen(false)} className="mr-2 px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300">Cancel</button>
            <button type="button" onClick={handleAddMember} disabled={!selectedUser} className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700 disabled:bg-indigo-300">Add Member</button>
        </div>
      </Modal>
    </div>
  );
};

export default GroupDetailPage;
