import React, { useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { getExpensesForGroup, getGroupBalances, reset } from '../features/expenses/expenseSlice';

const GroupDetailPage = () => {
  const { groupId } = useParams();
  const dispatch = useDispatch();

  const { expenses, balances, isLoading, isError, message } = useSelector(
    (state) => state.expenses
  );

  // Get the specific group from the groups state to display its name
  const { groups } = useSelector((state) => state.groups);
  const group = groups.find((g) => g._id === groupId);

  useEffect(() => {
    if (isError) {
      console.log(message);
    }

    dispatch(getExpensesForGroup(groupId));
    dispatch(getGroupBalances(groupId));

    return () => {
      dispatch(reset());
    };
  }, [groupId, isError, message, dispatch]);

  if (isLoading) {
    return <h2>Loading...</h2>;
  }

  return (
    <div className="p-8">
      <Link to="/" className="text-indigo-600 hover:text-indigo-800 mb-4 inline-block">
        &larr; Back to Dashboard
      </Link>
      <h1 className="text-3xl font-bold">{group ? group.name : 'Group Details'}</h1>
      
      <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-8">
        <div>
          <h2 className="text-2xl font-bold mb-4">Expenses</h2>
          {expenses.length > 0 ? (
            <ul className="space-y-4">
              {expenses.map((expense) => (
                <li key={expense._id} className="p-4 bg-white rounded-lg shadow-md">
                  <div className="flex justify-between">
                    <span className="font-bold">{expense.description}</span>
                    <span className="font-bold">₹{expense.amount.toFixed(2)}</span>
                  </div>
                  <div className="text-sm text-gray-600">
                    Paid by {expense.paidBy.name}
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No expenses have been added to this group yet.</p>
          )}
        </div>

        <div>
          <h2 className="text-2xl font-bold mb-4">Balances</h2>
          {balances.length > 0 ? (
            <ul className="space-y-2">
              {balances.map((member) => (
                <li key={member.email} className="flex justify-between p-2 rounded-md">
                  <span>{member.name}</span>
                  <span className={member.balance >= 0 ? 'text-green-600' : 'text-red-600'}>
                    {member.balance >= 0 ? `Gets back ₹${member.balance.toFixed(2)}` : `Owes ₹${Math.abs(member.balance).toFixed(2)}`}
                  </span>
                </li>
              ))}
            </ul>
          ) : (
            <p>Balances will be calculated once expenses are added.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default GroupDetailPage;
